package xml;

import javax.xml.bind.JAXBException;
import javax.xml.stream.XMLStreamException;

import org.dom4j.Document;
import org.dom4j.DocumentException;
import org.dom4j.DocumentHelper;

/**
 * Date: 2016年2月23日 下午6:10:28 <br/>
 * 
 * @author medusar
 */
public class XmlCreator {

	public static void main(String[] args) throws XMLStreamException, JAXBException, DocumentException {
		Entity entity = new Entity();
		entity.setStrData("<![CDATA[<bane>nmydata</bane>]]>");
		String xml = JaxbUtil.toXml(entity);
		System.out.println(xml);

		Entity obj = JaxbUtil.toObject(xml, Entity.class);
		System.out.println(obj.getStrData());
		
		Document doc = DocumentHelper.parseText("<?xml version=\"1.0\" encoding=\"UTF-8\"?><tripPackage version=\"1.0\" ssl=\"OA\"><technology></technology><businessContent></businessContent><signature><messageDigest>sGbWlYYf2IEUFc7BzWlY5IgjVqs=</messageDigest></signature></tripPackage>");
	}

}
